<template>
  <div class="cover-cart">
    <div class="container my-5">

      <a-page-header
        class="header white-theme"
        title="CART"
      />

      <a-table :columns="columns" :data-source="dataSource" :row-selection="rowSelection" class="border-cover white-theme">
        <span slot="nameAndThumbnail"  slot-scope="dataSource">
          <span class="cart-name">{{ dataSource.name }}</span>
          <img v-bind:src="dataSource.thumbnail" class="cart-thumbnail">
        </span>
        <span slot="amount">
          <a-input-number size="large" :min="1" :max="100000" :default-value="1" @change="amountChange" />
        </span>
        <span slot="action" slot-scope="text, record">
          <a-popconfirm
            v-if="dataSource.length"
            title="Sure to delete?"
            @confirm="() => deleteItem(record.key)"
          >
            <a href="javascript:;">Delete</a>
          </a-popconfirm>
        </span>
      </a-table>
      <!-- Table -->
    </div>
  </div>
</template>

<style lang='scss' scoped>
@import url("./style.scss");
</style>

<script>
const columns = [
  {
    title: 'Sản phẩm',
    dataIndex: 'nameAndThumbnail',
    key: 'nameAndThumbnail',
    scopedSlots: { customRender: 'nameAndThumbnail' },
  },
  {
    title: 'Đơn giá',
    dataIndex: 'price',
    key: 'price',
  },
  {
    title: 'Số lượng',
    dataIndex: 'amount',
    key: 'amount',
    scopedSlots: { customRender: 'amount' }
  },
  {
    title: 'Số tiền',
    dataIndex: 'total',
    key: 'total',
  },
  {
    title: 'Thao tác',
    key: 'action',
    scopedSlots: { customRender: 'action' },
  },
];
const dataSource = [
  {
    key: '1',
    nameAndThumbnail: {
      name: 'Giày custom songoku',
      thumbnail: 'data:image/jpeg;base64,'
    },
    price: 32,
    amount: 10,
    total: 1,
  },
  {
    key: '2',
    nameAndThumbnail: {
      name: 'Giày ninja',
      thumbnail: 'data:image/jpeg;base64,'
    },
    price: 42,
    amount: 10,
    total: 1,
  }
];
const rowSelection = {
  onChange: (selectedRowKeys, selectedRows) => {
    console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
  },
  onSelect: (record, selected, selectedRows) => {
    console.log(record, selected, selectedRows);
  },
  onSelectAll: (selected, selectedRows, changeRows) => {
    console.log(selected, selectedRows, changeRows);
  },
};
export default {
  layout: "cart",
  data() {
    return {
      dataSource,
      columns,
      rowSelection,
      products: [],
    }
  },
  mounted() {
    this.fetchProductsInStorage();
  },
  methods: {
    amountChange(value) {
      console.log(value)
    },
    deleteItem(key) {
      const dataSource = [...this.dataSource];
      this.dataSource = dataSource.filter(item => item.key !== key);
      // window.localStorage.setItem("products", this.dataSource)
    },
    fetchProductsInStorage() {
      const products = window.localStorage.getItem('products');
      if (!products) return;
      this.products = products.map(({name, thumbnail, ...product}) => 
        ({
          nameAndThumbnail: {
            name: name,
            thumbnail: thumbnail,
          },
          ...product
        })
      )
    }
  }
}
</script>

<style lang="scss" scoped>
@import url('./style.scss');
</style>